import Card from '../UI/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';
import { useDispatch, useSelector } from 'react-redux';
const Cart = (props) => {
  const productsInCart = useSelector(state =>state.quantity.items)
  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
      {!productsInCart.map((product =><CartItem key={product.id}
          item={{ title: product.name, quantity:product.quantity, total: 18, price:product.price, id:product.id }}
        />)) }
      </ul>
    </Card>
  );
};

export default Cart;
